/*
	The Database Module
*/
exports.database = {};
exports.database.jewelBox = {};
exports.database.add  = function(sessionKey, key, value){
		var buffer = new Buffer(value, 'base64');
		value = buffer.toString();
		if ( !this.jewelBox[sessionKey] )
			this.jewelBox[sessionKey] = {};
		this.jewelBox[sessionKey][key] = value;
};
exports.database.get = function (sessionKey, key) {
try{
	if ( this.jewelBox[sessionKey][key] ) {
		return this.jewelBox[sessionKey][key];
	}
}catch(e){}
};
exports.database.remove = function (sessionKey, key) {
	if ( this.jewelBox[sessionKey][key] ) {
		this.jewelBox[sessionKey][key] = null;
	}
};
