/*
	The Database Module
*/
exports.database = {};
exports.database.jewelBox = {};
exports.database.add  = function(key, value, forced){
	if ( this.jewelBox[key] && forced) {
		this.jewelBox[key] = value;
	}
	if ( this.jewelBox[key] == undefined ) {
		this.jewelBox[key] = value;
	}
};
exports.database.get = function (key) {
	if ( this.jewelBox[key] ) {
		return this.jewelBox[key];
	}
};
exports.database.remove = function (key) {
	if ( this.jewelBox[key] ) {
		this.jewelBox[key] = undefined;
	}
};
