var users = require("../config/users.js").users;
var crypto = require("crypto");
exports.user = {
	validate: function(username, password) {
		if ( users[username] ) {
			if ( crypto.createHash('md5').update(users[username].password).digest('hex') == password ) {
				console.log("[ * ] User login : "+username+" at "+(new Date()).toString());
				return true;
			} else {
				return false;
			}
		}
		return false;
	}
};