/*
	Users Priv's and Access
*/
exports.users = {
	__proto__: {
		__init__: function(){
			if ( !this.isInit ) {
				console.log("Getting users list..");
				var Ajax = new XMLHttpRequest();
				Ajax.open("GET", "../users.json", true);
				Ajax.onreadystatechange = function() {
					if ( this.readyState == 4 ) {
						if ( this.status == 200 ) {
							var dummyText = eval(this.responseText);
							var dummyObject = {};
							for ( a in dummyText ) {
								dummyObject[a] = dummyText[a];
							}
							this.jewelBox = dummyObject;
							this.isInit = true;
						}
					}
				}
			}
		},
		isInit: false,
		jewelBox: {},
		getUser: function(username){
			if ( this.jewelBox[username] ) {
				return this.jewelBox[username];
			} else {
				return false;
			}
		},
		changePassword: function( username ) {
			/*
				Get the position where the username is
			*/
			
		}
	}
};